import React from "react";
import ReactDOM from "react-dom";
import HomePage from "./HomePage";
import LoginPage from "./LoginPage";
import PostPage from "./PostPage";
import CommentsSection from "./CommentsSection";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentView: "home",
      currentPostId: null,
    };
  }

  handleViewChange = (view, postId) => {
    this.setState({ currentView: view, currentPostId: postId });
  };

  render() {
    const { currentView, currentPostId } = this.state;

    let currentComponent;
    switch (currentView) {
      case "home":
        currentComponent = <HomePage onViewChange={this.handleViewChange} />;
        break;
      case "login":
        currentComponent = <LoginPage onViewChange={this.handleViewChange} />;
        break;
      case "post":
        currentComponent = (
          <PostPage
            onViewChange={this.handleViewChange}
            postId={currentPostId}
          />
        );
        break;
      case "comments":
        currentComponent = (
          <CommentsSection
            onViewChange={this.handleViewChange}
            postId={currentPostId}
          />
        );
        break;
      default:
        currentComponent = <HomePage onViewChange={this.handleViewChange} />;
        break;
    }

    return <div>{currentComponent}</div>;
  }
}

ReactDOM.render(<App />, document.getElementById("root"));
